import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

function App() {
const[blog, setBlog] = useState("");


const[wordsLength, setWordsLength] = useState([])

  const handleSubmit = (e) =>{
    e.preventDefault()

    let {text} = blog

    if (blog.length > 2){
       alert("words exceed the capacity")
    }else if(blog.length === 0) { 
      alert('please input something')


    }else{
      setWordsLength(
      [...wordsLength, {blog:blog, len:blog.split(' ').length}]

      )
    }
    // var newLs = [...wordsLength, {text:blog, len:blog.split(' ').length}]
    // setWordsLength(newLs)  

    // console.log('words',newLs)
  }

  const handelChange = (e) =>{
    setBlog(e.target.value)
    console.log('e.target.value', e.target.value);
  }


  return (
    <div className="wordCounter">
      <div>
        <textarea
        value={blog} name="blog" onChange={handelChange}
        ></textarea><br />
        <button onClick={handleSubmit}>
          submit
        </button>
      </div>
      {

       <ul>
       {wordsLength.map((value,index)=>{
         return(
           <div key={index}>
           <p>{value.text}</p>
           <p>{value.len}</p>
           </div>
         )
       })}
      </ul> }

    </div>
  );
}

export default App;
