import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

function App() {
  const [blog, setBlog] = useState({

    text: "", 
    length: 0,


  });


  const [wordsLength, setWordsLength] = useState([])

  const handleSubmit = (e) => {
    e.preventDefault()

    let { text, length } = blog
    // let limit = 2;

    if (length > 2) {
      alert("words exceed the capacity")

    } else if (text.length === 0) {
      alert('please input something')


    } else {
      setWordsLength(
        [...wordsLength, blog]

        )
        setBlog(
          "", [...wordsLength, blog]
        )
      }
      // var newLs = [...wordsLength, {text:blog, len:blog.split(' ').length}]
      // setWordsLength(newLs)  

      // console.log('words',newLs)
    }

    const handelChange = (e) => {
    //   let { text, length } = blog
    // let limit = 2;
    //   console.log("anele",text.slice(0, limit).trim().split(" "))
    //   e.stopPropagation();
    setBlog({
      text:e.target.value,
      length: e.target.value.trim().split(' ').length
    })

  }


  return (
    <div className="wordCounter">
      <div>
        <textarea
        disabled={blog.text.split(' ').length > 2}
          value={blog.text} name="blog" onChange={handelChange}
        ></textarea><br />
        <button 
        onClick={handleSubmit}
        >
          submit
        </button>
      </div>
      {

        <ul>
         {wordsLength.map((value, index) => {
            return (
              <div key={index}>
                <p>{value.text}</p>
                <p>{value.length}</p>
              </div>
            )
          })}
        </ul>} 

    </div>
  );
}

export default App;
