import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

function App() {
const[blog, setBlog] = useState("");


const[wordsLength, setWordsLength] = useState({"blog":[]})

  const handleSubmit = (e) =>{
    e.preventDefault();
    setWordsLength([...blog, wordsLength])


  }


  return (
    <div className="wordCounter">
      <form onSubmit={handleSubmit} >
        <textarea maxlength="10" rows="3" 
        value={blog} onChange={(e)=> setBlog(e.target.value)}
        ></textarea><br />
        <button>
          submit
        </button>
      </form>
      <ul>
       {wordsLength.map((value,index)=>{
         return(
           <div key={index}>
           <li>{value.blog}</li>
           </div>
         )
       })}
      </ul>
    </div>
  );
}

export default App;
