import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

function App() {
  const [blog, setBlog] = useState({

    text: "", 
    length: 0,


  });


  const [wordsLength, setWordsLength] = useState([])

  const handleSubmit = (e) => {
    e.preventDefault()

    let { text, length } = blog

    if (length > 2) {
      // return text.slice(0, limit)
      alert("words exceed the capacity")
    } else if (text.length === 0) {
      alert('please input something')


    } else {
      setWordsLength(
        [...wordsLength, blog]

      )
    }
    // var newLs = [...wordsLength, {text:blog, len:blog.split(' ').length}]
    // setWordsLength(newLs)  

    // console.log('words',newLs)
  }

  const handelChange = (e) => {
    setBlog({
      text:e.target.value,
      length: e.target.value.trim().split(' ').length
    })
    console.log('Angel', e.target.value.trim().split(' ').length);
  }


  return (
    <div className="wordCounter">
      <div>
        <textarea
          value={blog.text} name="blog" onChange={handelChange}
        ></textarea><br />
        <button 
        onClick={handleSubmit}
        >
          submit
        </button>
      </div>
      {

        <ul>
         {wordsLength.map((value, index) => {
            return (
              <div key={index}>
                <p>{value.text}</p>
                <p>{value.length}</p>
              </div>
            )
          })}
        </ul>} 

    </div>
  );
}

export default App;
